import ACCESS_ENUM from "@/access/accessEnum";

/**
 * Ê£ÄÊü•ÊùÉÈôêÔºàÂà§Êñ≠ÂΩìÂâçÁôªÂΩïÁî®Êà∑ÊòØÂê¶ÂÖ∑ÊúâÊüê‰∏™ÊùÉÈôêÔºâ
 * @param loginUser ÂΩìÂâçÁôªÂΩïÁî®Êà∑
 * @param needAccess ÈúÄË¶ÅÊúâÁöÑÊùÉÈôê
 * @return boolean ÊúâÊó†ÊùÉÈôê
 */
const checkAccess = (loginUser: any, needAccess = ACCESS_ENUM.NOT_LOGIN) => {
  // Ëé∑ÂèñÂΩìÂâçÁôªÂΩïÁî®Êà∑ÂÖ∑ÊúâÁöÑÊùÉÈôêÔºàÂ¶ÇÊûúÊ≤°Êúâ loginUserÔºåÂàôË°®Á§∫Êú™ÁôªÂΩïÔºâ
  const loginUserAccess = loginUser?.userRole ?? ACCESS_ENUM.NOT_LOGIN;
  if (needAccess === ACCESS_ENUM.NOT_LOGIN) {
    return true;
  }
  // Â¶ÇÊûúÁî®Êà∑ÁôªÂΩïÊâçËÉΩËÆøÈóÆ
  if (needAccess === ACCESS_ENUM.USER) {
    // Â¶ÇÊûúÁî®Êà∑Ê≤°ÁôªÂΩïÔºåÈÇ£‰πàË°®Á§∫Êó†ÊùÉÈôê
    if (loginUserAccess === ACCESS_ENUM.NOT_LOGIN) {
      return false;
    }
  }
  // Â¶ÇÊûúÈúÄË¶ÅÁÆ°ÁêÜÂëòÊùÉÈôê
  if (needAccess === ACCESS_ENUM.ADMIN) {
    // Â¶ÇÊûú‰∏ç‰∏∫ÁÆ°ÁêÜÂëòÔºåË°®Á§∫Êó†ÊùÉÈôê
    if (loginUserAccess !== ACCESS_ENUM.ADMIN) {
      return false;
    }
  }
  return true;
};

export default checkAccess;

